    useEffect(()=>{
        getAllCategories();
    },[])
    return(
      <div>
      <div className= {styles.container}>
        <div className = {styles.buttonContainer}><p className = {styles.createCategoryButton}>Create Category</p></div>
        <div>
            {console.log(categories.length)}
        {categories.map((cate) =>{
